export function getWindowSize() {
    const main_div = document.getElementById('root');
    const innerWidth = main_div.clientWidth;
    const innerHeight = Math.max(main_div.clientHeight, 900);

    return {innerWidth, innerHeight};
}

export function dec2bin(dec, n) {
  dec = Number(dec);
  var res = dec.toString(2);

  if (res.length < n) {
    for (let i = 0; i <= (n - res.length); ++i) {
      res = '0' + res;
    }
  }
  return res;
}

export function multiplyMatrix(a, b) {
  var aNumRows = a.length, aNumCols = a[0].length,
      bNumRows = b.length, bNumCols = b[0].length,
      m = new Array(aNumRows);  // initialize array of rows
  for (var r = 0; r < aNumRows; ++r) {
    m[r] = new Array(bNumCols); // initialize the current row
    for (var c = 0; c < bNumCols; ++c) {
      m[r][c] = 0;             // initialize the current cell
      for (var i = 0; i < aNumCols; ++i) {
        m[r][c] += a[r][i] * b[i][c];
      }
    }
  }
  return m;
}

export function rad2degrees(radians) {
  return radians * 180 / Math.PI;
}

export function getfi(theta, h, M, i) {

  i += 1;
  var fi = i * theta;
  if (i <= h && i % 2 === 0) {
    fi = i * theta;
  }
  else if (i <= h && i % 2 === 1) {
    fi = (Math.ceil(h) - 1 + i) * theta;
  } else if (i > h && (M - 1 - i) % 2 === 0) {
    fi = (M - i) * theta;
  } else if (i > h && (M - 1 - i) % 2 === 1) {
    fi = (Math.ceil(h) - 1 + M - i) * theta;
  }

  console.log(fi);
  return fi;
}
