import React from 'react';
import App from './App';
import { StateApp } from './StateApp.ts';
import Visualise from './Visualise';
import axios from "axios";

export default class LoadAeon extends React.Component {
    state = {
        value : this.props.value || StateApp.LoadAeon,
        selectedFile: null,
        resultData: null,

        computing: false,

        semantics: null,
        option: null,

        nodes_json: null,
        json_data:null,
    };

    onFileChange = event => {
        // Update the state
        this.setState({ selectedFile: event.target.files[0] });
    };

    // On file upload (click the upload button)
    onFileUpload = () => {
          const file = this.state.selectedFile;

          var reader = new FileReader();
          reader.readAsText(file,'UTF-8');

          reader.addEventListener("load", e => {
              this.setState({ resultData : reader.result });
            //  this.setState({ value : StateApp.Visualise });
          });

        this.setState({ value : StateApp.Config });
      };


    fileData = () => {

        if (this.state.selectedFile) {

          return (
            <div>
              <h2>File Details:</h2>

                <p>File Name: {this.state.selectedFile.name}</p>
                <p>File Type: {this.state.selectedFile.type}</p>

            </div>
          );
        } else {
          return (
            <div>
              <br />
              <h4>Choose before Pressing the Upload button</h4>
            </div>
          );
        }
    };

    handleBackButton = event => {
      this.setState({ value : StateApp.MainApp });
    };

    handleSemanticsButton = event => {
      event.preventDefault();
      this.setState({ semantics : this.state.async ? 1 : 2 });
    };

    handleOptionButton = event => {
      event.preventDefault();
      this.setState({ option : this.state.node ? 1 : 2 });
    };

    render() { 

        if (this.state.value === StateApp.MainApp) {
          return <App />;
        }

        if (this.state.value === StateApp.Visualise) {
            return <Visualise />;
        }  

        if (this.state.value === StateApp.Config) {
          if (this.state.json_data !== null) {
            // ask to save or only visualize
            return <Visualise fileData={this.state.json_data} />;
          }

          if (this.state.semantics === null) {
            return (
            <form>
              <input type="radio" id="async" name="semantics" value="1" onChange={event => this.setState({async : true, sync: false})}/>
              <label for="async">Async</label>
              <input type="radio" id="sync" name="semantics" value="2" onChange={event => this.setState({sync : true, async: false})}/>
              <label for="sync">Sync</label>
              <input type="submit" value="Send" onClick={this.handleSemanticsButton} />
            </form>);
          }

          if (this.state.option === null) {
            return (
              <div>
                <input type="radio" id="node" name="option" value="1" onChange={event => this.setState({node : true, whole: false})}/>
                <label for="node">from one node</label>
                <input type="radio" id="whole" name="option" value="2" onChange={event => this.setState({whole : true, node: false})}/>
                <label for="whole">whole state space</label>
                <input type="submit" value="Send" onClick={this.handleOptionButton} />
              </div>);
          }

          if (this.state.option == 1 && this.state.nodes_json === null) {
            axios
              .get("http://127.0.0.1:8000/get_nodes=" + this.state.resultData)
              .then(response => this.setState({ nodes_json : response.data }));
              console.log(this.state.nodes_json);
            return (
                <div>
                  <h3>Getting nodes</h3>
                </div>
            );
          }          

          if (!this.state.computing) {
            /*
            axios
              .get("http://127.0.0.1:8000/get_data")
              .then(response => this.setState({ resultData : response.data }));
            console.log(this.state.resultData);
            */
            return (
            <div>
              <h3>Sending request</h3>
            </div>
            );
          }


          return (
            <div>
              <h3>Waiting for computation</h3>
            </div>
          );

        }

        return (
            <div>
                <h3>
                  File Upload
                </h3>
                <div>
                    <input type="file" onChange={this.onFileChange} accept=".aeon"/>
                    <button onClick={this.onFileUpload}>
                      Process
                    </button>
                </div>
              {this.fileData()}

              <input type="submit" value="Back" onClick={this.handleBackButton} />
            </div>
          );
    }
}

//export default LoadAeon;
